const apiKey = 'f0f5Tcg-ShIqQEBvDJUSRYTQXUhvmmT1fCUgere5XGsrI6mOVqyL9gWBt8a1c5S4HDW8L0vms4obdB51XlE4CkMi8UVz_usFUmZHGhoFWOw5WAjyzUraXOpZA8r9W3Yx';

Yelp = {
  endpoint: `https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=${term}&location=${location}&sort_by=${sortBy}`,
  search: function(term, location, sortBy) {
    return fetch(endpoint, {
      headers: `Bearer ${apiKey}`
    }).then(response => {
      return response.json();
    }).then(jsonResponse => {
      if (jsonResponse.businesses) {
        return jsonResponse.businesses.map(business => {
          return {
            id: business.id,
            imageSrc: business.image_url,
            name: business.name,
            address: business.location.address1,
            city: business.location.city,
            state: business.location.state,
            zipCode: business.location.zip_code,
            category: business.categories[0].title,
            rating: business.rating,
            reviewCount: business.review_count
          }
        })
      }
    });
  }
};

export default Yelp;
